
// 대입연산자
public class 이항연산자_대입연산자1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=10;
		a+=100; // a값 100 증가
		System.out.println(a); // 110
		
		a-=50; // a값 50 감소
		System.out.println(a); // 60
		
		a*=20; // a값 20 곱하기
		System.out.println(a); // 1200
		
		a/=3; // a값 3 나누기
		System.out.println(a); // 400
	}

}
